let slides=document.querySelectorAll(".card-slides p"),arrows=document.querySelectorAll(".arr"),arrUp=document.querySelector(".arr-up"),arrDown=document.querySelector(".arr-down"),sliderLine=document.querySelector(".card-slides"),img=document.querySelectorAll(".card-slides p"),mainImg=document.querySelector(".card-main-img a"),imgBg=document.querySelector(".full-img"),close=document.querySelector(".close-img");function slideUp(e){let l=document.querySelectorAll(".card-slides p");e.preventDefault(),deletedImg=l[l.length-1],sliderLine.removeChild(l[l.length-1]),sliderLine.insertBefore(deletedImg,sliderLine.firstElementChild)}function slideDown(e){let l=document.querySelectorAll(".card-slides p");e.preventDefault(),deletedImg=l[0],sliderLine.removeChild(l[0]),sliderLine.appendChild(deletedImg)}function showImg(e){e.preventDefault(),mainImg.innerHTML=`<img src="${this.querySelector("img").getAttribute("src")}" alt="головне фото товару">`}function openImg(e){e.preventDefault(),imgBg.style.display="block",imgBg.querySelector(".full-main-img").innerHTML=this.querySelector("img").outerHTML,close.onclick=bgClose,imgBg.onclick=bgClose}function bgClose(e){e.target!=imgBg.querySelector("img")&&(imgBg.style.display="none")}slides.length>3&&arrows.forEach(e=>{e.style.display="flex"}),arrUp.onclick=slideUp,arrDown.onclick=slideDown,img.forEach(e=>{e.onclick=showImg}),mainImg.onclick=openImg;